DROP TABLE IF EXISTS `bg_db_relation`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `bg_db_relation` (
  `id` int NOT NULL AUTO_INCREMENT,
  `dbconf_name` varchar(16) NOT NULL DEFAULT '' COMMENT '主表 db',
  `relation_table_name` varchar(64) NOT NULL DEFAULT '' COMMENT '关联表  表名',
  `relation_left_field` varchar(32) NOT NULL DEFAULT '' COMMENT '关联表 left表值的字段名',
  `relation_right_field` varchar(32) NOT NULL DEFAULT '' COMMENT '关联表 right表值的字段名',
  `relation_ext_field` varchar(32) NOT NULL DEFAULT '' COMMENT '关联表额外字段，用于综合索引表，所谓综合索引表 比如  db_op_log 就是类似，right 端为多个表，不是只有一个表，本质上应该设计成多个简单关联表，但愣是压成了一张表',
  `relation_ext_field_val` varchar(32) NOT NULL DEFAULT '' COMMENT '关联表额外字段 的值，用于索引表，至于为什么不把  relation_ext_field 和 relation_ext_field_val 合成一个 reation_ext_sql 只是为了防止后期忍不住破坏规矩',
  `relation_res_key` varchar(32) NOT NULL DEFAULT '' COMMENT '关联结果 以 xx 为key',
  `relation_res_title` varchar(32) NOT NULL DEFAULT '' COMMENT '关联结果 title',
  `relation_type` varchar(32) NOT NULL DEFAULT '' COMMENT '关联关系 has_one ,has_many  不考虑 多对一 和 多对多',
  `query_input_type` varchar(32) NOT NULL DEFAULT 'text' COMMENT '查询数据类型',
  `left_table_name` varchar(64) NOT NULL DEFAULT '' COMMENT 'left  表名',
  `left_table_index_field` varchar(32) NOT NULL DEFAULT '' COMMENT 'left 表 字段名',
  `right_table_name` varchar(64) NOT NULL DEFAULT 'right 表名',
  `right_table_index_field` varchar(64) NOT NULL DEFAULT '' COMMENT 'right表 字段名  ',
  `right_table_label_field` varchar(64) NOT NULL DEFAULT '' COMMENT '从right 表中 取出一个字段作为label, src_safe_columns取的信息会作为 info 附加上去(于label同级)',
  `right_table_info_fields` varchar(512) NOT NULL DEFAULT '' COMMENT '用于解释表中，有些关联字段，是敏感的，得去掉',
  `is_right_as_filter` tinyint NOT NULL DEFAULT '2' COMMENT 'right是否作为 筛选项',
  `filter_val_items_length` smallint NOT NULL DEFAULT '100' COMMENT 'right表作为选项的最大个数，比如说right 是标签，作为选项可能几千个还好，但是万一是几十万个呢？ 必须设置一个上限',
  `is_ok` tinyint NOT NULL DEFAULT '1' COMMENT '是否正常  1:正常  2:被封禁',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `uni` (`dbconf_name`,`left_table_name`,`relation_res_key`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='关联信息，仅限于同库，也不考虑字段多含义的情况，比如markedboat的op_record 及 collection 又是文章、短消息、文件的情况';
